                }

                RecoverComposure(Time.deltaTime);

                if (time <= _lastPanicExitTime + PanicCooldown)
                    return;

                if (ShouldPanicFromThreat())
                {
                    Vector3 retreat = -_bot.LookDirection.normalized;
                    TryStartPanic(time, retreat);
