            unchecked
            {
                hash = (int)((hash ^ (hash >> 13)) * 0x7FEDCBA9);
                float dx = ((hash & 0xFF) / 255f - 0.5f) * 0.09f;
                float dz = (((hash >> 8) & 0xFF) / 255f - 0.5f) * 0.09f;
                return new Vector3(pos.x + dx, pos.y, pos.z + dz);
