                Vector3 pos = bot.Position;
                float radius = DefaultRadius * Mathf.Clamp(cohesionScale, 0.7f, 1.3f);
                if ((pos.x - safeTarget.x) * (pos.x - safeTarget.x) + (pos.z - safeTarget.z) * (pos.z - safeTarget.z) < radius * radius)
