            return state == EPlayerState.Loot ||
                   state == EPlayerState.Pickup ||
                   state == EPlayerState.Open ||
                   state == EPlayerState.Close ||
                   state == EPlayerState.Unlock ||
                   state == EPlayerState.Plant ||
