                var hotspot = new Hotspot(point.Position, point.Zone);
                All.Add(hotspot);

                if (!ByZone.TryGetValue(point.Zone, out List<Hotspot>? zoneList))
                {
                    zoneList = new List<Hotspot>(8);
                    ByZone[point.Zone] = zoneList;
                }

                zoneList.Add(hotspot);
