namespace AIRefactored.AI.Medical
{
    using System;
    using AIRefactored.AI.Core;
    using AIRefactored.AI.Helpers;
    using AIRefactored.Core;
    using BepInEx.Logging;
    using EFT;
    using EFT.HealthSystem;
    using UnityEngine;

    /// <summary>
    /// Arbitration-driven squad healing and support overlays.
    /// Detects injured squadmates and issues event-driven healing/aid overlays with:
    /// – Human-like indecision, pose/personality/context awareness, error isolation.
    /// – Overlay arbitration ensures zero conflicts, spam, or disables.
    /// – Never per-tick/coroutine; only BotBrain-ticked. Bulletproof for SPT/FIKA/headless.
    /// </summary>
    public sealed class BotGroupHealCoordinator
    {
        #region Constants

        private const float HealCheckInterval = 3.5f; // Arbitration/event interval (seconds)
        private const float HealthThreshold = 0.65f;  // Body part % to trigger heal
        private const float HealTriggerRange = 12.5f; // Range (meters) for squad healing
        private static readonly float HealTriggerRangeSqr = HealTriggerRange * HealTriggerRange;
        private const float SupportVoiceCooldown = 5.5f;

        #endregion

        #region Fields

        private readonly BotOwner _bot;
        private readonly BotComponentCache _cache;
        private float _nextCheckTime;
        private float _lastSupportVoiceTime;
        private static readonly EBodyPart[] BodyParts = (EBodyPart[])Enum.GetValues(typeof(EBodyPart));
        private static readonly ManualLogSource Logger = Plugin.LoggerInstance;
        private bool _isActive = true;

        #endregion

        #region Construction

        /// <summary>
        /// Wires up the group heal coordinator for the specified bot/cache.
        /// </summary>
        public BotGroupHealCoordinator(BotComponentCache cache)
        {
            if (cache == null || cache.Bot == null)
            {
                _isActive = false;
                Logger.LogError("[BotGroupHealCoordinator] Initialization failed: cache or bot is null. Disabling group heal logic for this bot.");
                return;
            }

            _cache = cache;
            _bot = cache.Bot;
            _isActive = true;
            _lastSupportVoiceTime = -99f;
        }

        #endregion

        #region Public API

        /// <summary>
        /// Arbitration/event-driven squad healing overlay logic. BotBrain-ticked only.
        /// </summary>
        public void Tick(float now)
        {
            if (!_isActive || _bot == null || _bot.IsDead || _bot.BotsGroup == null || now < _nextCheckTime)
                return;

            _nextCheckTime = now + HealCheckInterval;

            try
            {
                int memberCount = _bot.BotsGroup.MembersCount;
                if (memberCount <= 1)
                    return;

                for (int i = 0; i < memberCount; i++)
                {
                    BotOwner mate = _bot.BotsGroup.Member(i);
                    if (!IsValidMate(mate))
                        continue;

                    Player matePlayer = EFTPlayerUtil.ResolvePlayer(mate);
                    if (!EFTPlayerUtil.IsValidGroupPlayer(matePlayer))
                        continue;

                    // Never aid if mate is currently fighting
                    if (mate.Memory != null && mate.Memory.GoalEnemy != null)
                        continue;

                    IHealthController health = matePlayer.HealthController;
                    if (health == null || !health.IsAlive || !NeedsHealing(health))
                        continue;

                    // If self is panicking or fighting, don't heal
                    if (_cache.PanicHandler != null && _cache.PanicHandler.IsPanicking)
                        continue;
                    if (_bot.Memory != null && _bot.Memory.GoalEnemy != null)
                        continue;

                    // Human-like squad indecision: random pass for realism
                    if (memberCount > 2 && UnityEngine.Random.value < 0.10f)
                        continue;

                    // Arbitration overlay: Only one heal overlay per bot at a time
                    if (!BotOverlayManager.CanIssueMove(_bot, BotOverlayType.Medical))
                        continue;

                    // Attempt squad healing overlay (never disables or per-ticks)
                    if (_cache.SquadHealer != null && !_cache.SquadHealer.IsInProcess)
                    {
                        IPlayer safeTarget = EFTPlayerUtil.AsSafeIPlayer(matePlayer);
                        _cache.SquadHealer.HealAsk(safeTarget);

                        BotOverlayManager.RegisterMove(_bot, BotOverlayType.Medical);
                        TrySaySupport(EPhraseTrigger.Cooperation, now);
                        return;
                    }

                    // If healing not possible, at least voice support
                    TrySaySupport(EPhraseTrigger.NeedHelp, now);
                }
            }
            catch (Exception ex)
            {
                _isActive = false;
                Logger.LogError($"[BotGroupHealCoordinator] Tick() failed: {ex}. Disabling group heal logic for this bot.");
            }
        }

        #endregion

        #region Private Methods

        /// <summary>
        /// Returns true if mate is alive, in range, not self, and valid for aid.
        /// </summary>
        private bool IsValidMate(BotOwner mate)
        {
            if (mate == null || mate.IsDead || ReferenceEquals(mate, _bot))
                return false;

            try
            {
                Player selfPlayer = EFTPlayerUtil.ResolvePlayer(_bot);
                Player matePlayer = EFTPlayerUtil.ResolvePlayer(mate);
                if (selfPlayer == null || matePlayer == null)
                    return false;

                Vector3 selfPos = EFTPlayerUtil.GetPosition(selfPlayer);
                Vector3 matePos = EFTPlayerUtil.GetPosition(matePlayer);
                float dx = matePos.x - selfPos.x;
                float dz = matePos.z - selfPos.z;
                float distSqr = (dx * dx) + (dz * dz);

                return distSqr <= HealTriggerRangeSqr;
            }
            catch (Exception ex)
            {
                Logger.LogError($"[BotGroupHealCoordinator] IsValidMate() failed: {ex}");
                return false;
            }
        }

        /// <summary>
        /// True if any body part is below heal threshold (triage logic).
        /// </summary>
        private static bool NeedsHealing(IHealthController health)
        {
            try
            {
                for (int i = 0; i < BodyParts.Length; i++)
                {
                    EBodyPart part = BodyParts[i];
                    ValueStruct hp = health.GetBodyPartHealth(part);
                    if (hp.Maximum > 0f && hp.Current < hp.Maximum * HealthThreshold)
                        return true;
                }
            }
            catch (Exception ex)
            {
                Logger.LogError($"[BotGroupHealCoordinator] NeedsHealing() failed: {ex}");
            }
            return false;
        }

        /// <summary>
        /// Anti-spam voice logic for realism, with cooldown.
        /// </summary>
        private void TrySaySupport(EPhraseTrigger phrase, float now)
        {
            try
            {
                if (_bot != null && _bot.BotTalk != null && (now - _lastSupportVoiceTime > SupportVoiceCooldown))
                {
                    _bot.BotTalk.TrySay(phrase);
                    _lastSupportVoiceTime = now;
                }
            }
            catch (Exception ex)
            {
                Logger.LogError($"[BotGroupHealCoordinator] TrySaySupport() failed: {ex}");
            }
        }

        #endregion
    }
}
