namespace AIRefactored.AI.Missions.Subsystems
{
    using System;
    using System.Collections.Generic;
    using AIRefactored.AI.Core;
    using AIRefactored.AI.Groups;
    using AIRefactored.AI.Helpers;
    using AIRefactored.AI.Navigation;
    using AIRefactored.Core;
    using AIRefactored.Pools;
    using AIRefactored.Runtime;
    using BepInEx.Logging;
    using EFT;
    using EFT.InventoryLogic;
    using EFT.Interactive;
    using UnityEngine;
    using UnityEngine.AI;

    /// <summary>
    /// Overlay-only mission intent logic: no per-frame movement, pooled, squad-aware, error-isolated.
    /// All pooling strictly uses TempListPool. Fallbacks use only AIRefactored helpers, never disables.
    /// </summary>
    public sealed class MissionEvaluator
    {
        #region Constants

        private const int LootItemCountThreshold = 40;
        private const float SquadCohesionRange = 10f;
        private const float StuckCooldown = 30f;
        private const float StuckDuration = 25f;
        private const float MinMoveThreshold = 0.3f;
        private const float NavSampleRadius = 1.3f;

        #endregion

        #region Fields

        private static readonly ManualLogSource Logger = Plugin.LoggerInstance;

        private readonly BotOwner _bot;
        private readonly BotComponentCache _cache;
        private readonly BotGroupSyncCoordinator _group;
        private readonly BotPersonalityProfile _profile;

        private int _fallbackAttempts;
        private float _lastMoveTime;
        private float _stuckSince;
        private float _lastStuckFallbackTime;
        private Vector3 _lastPos;

        #endregion

        #region Constructor

        public MissionEvaluator(BotOwner bot, BotComponentCache cache)
        {
            if (!EFTPlayerUtil.IsValidBotOwner(bot) || cache == null)
                throw new ArgumentException("[MissionEvaluator] Invalid bot or cache.");

            _bot = bot;
            _cache = cache;
            _profile = BotRegistry.Get(bot.ProfileId);
            _group = BotCacheUtility.GetGroupSync(cache);
            _lastPos = EFTPlayerUtil.GetPosition(bot);
            _lastMoveTime = Time.time;
            _fallbackAttempts = 0;
            _stuckSince = Time.time;
            _lastStuckFallbackTime = -99f;
        }

        #endregion

        #region Intent Logic

        /// <summary>
        /// Returns overlay intent direction for movement overlays. Squad-aware. Never allocates.
        /// </summary>
        public Vector3 GetCurrentIntentDirection()
        {
            if (!EFTPlayerUtil.IsValidBotOwner(_bot))
                return Vector3.zero;

            if (_cache.GroupBehavior == null || !_cache.GroupBehavior.IsInSquad)
                return _bot.LookDirection.normalized;

            Vector3 squadIntent = _cache.GroupBehavior.GetSquadFlockIntent();
            if (squadIntent.sqrMagnitude > 0.01f)
                return squadIntent.normalized;

            return _bot.LookDirection.normalized;
        }

        #endregion

        #region Mission Checks

        public bool IsGroupAligned()
        {
            try
            {
                if (_group == null)
                    return true;

                IReadOnlyList<BotOwner> squad = _group.GetTeammates();
                if (squad == null || squad.Count == 0)
                    return true;

                int nearby = 0;
                Vector3 selfPos = EFTPlayerUtil.GetPosition(_bot);
                float sqrRange = SquadCohesionRange * SquadCohesionRange;

                for (int i = 0; i < squad.Count; i++)
                {
                    BotOwner mate = squad[i];
                    if (mate != null && !mate.IsDead &&
                        (EFTPlayerUtil.GetPosition(mate) - selfPos).sqrMagnitude <= sqrRange)
                    {
                        nearby++;
                    }
                }

                return nearby >= Mathf.CeilToInt(squad.Count * 0.6f);
            }
            catch (Exception ex)
            {
                Logger.LogError($"[MissionEvaluator] IsGroupAligned failed: {ex}");
                return true;
            }
        }

        public bool ShouldExtractEarly()
        {
            try
            {
                if (_profile == null || _profile.IsFrenzied || _profile.Caution <= 0.6f)
                    return false;

                Player player = _bot.GetPlayer;
                if (player?.Inventory?.Equipment == null)
                    return false;

                Slot backpackSlot = player.Inventory.Equipment.GetSlot(EquipmentSlot.Backpack);
                if (backpackSlot?.ContainedItem == null)
                    return false;

                List<Item> items = TempListPool.Rent<Item>();
                try
                {
                    backpackSlot.ContainedItem.GetAllItemsNonAlloc(items);
                    float fullness = (float)items.Count / LootItemCountThreshold;
                    return fullness >= _profile.RetreatThreshold;
                }
                finally
                {
                    TempListPool.Return(items);
                }
            }
            catch (Exception ex)
            {
                Logger.LogError($"[MissionEvaluator] ShouldExtractEarly failed: {ex}");
                return false;
            }
        }

        /// <summary>
        /// Overlay/event-based extraction attempt. Overlay-only; never sets position directly.
        /// </summary>
        public void TryExtract()
        {
            try
            {
                ExfiltrationPoint[] points = GameObject.FindObjectsOfType<ExfiltrationPoint>();
                if (points == null || points.Length == 0)
                    return;

                Vector3 myPos = EFTPlayerUtil.GetPosition(_bot);
                float minDist = float.MaxValue;
                ExfiltrationPoint best = null;

                for (int i = 0; i < points.Length; i++)
                {
                    ExfiltrationPoint point = points[i];
                    if (point != null && point.Status == EExfiltrationStatus.RegularMode)
                    {
                        float dist = (point.transform.position - myPos).sqrMagnitude;
                        if (dist < minDist)
                        {
                            minDist = dist;
                            best = point;
                        }
                    }
                }

                if (best != null &&
                    NavMesh.SamplePosition(best.transform.position, out NavMeshHit hit, NavSampleRadius, AIRefactoredLayerMasks.NavMeshAll))
                {
                    Vector3 drifted = BotMovementHelper.ApplyMicroDrift(hit.position, _bot.ProfileId, Time.frameCount, _profile);
                    // Overlay/event-driven intent ONLY (not per-frame, not direct set)
                    BotMovementHelper.SmoothMoveToSafe(_bot, drifted, slow: true, cohesion: 1f);

                    if (_bot.BotTalk != null && UnityEngine.Random.value < 0.45f)
                    {
                        try { _bot.BotTalk.TrySay(EPhraseTrigger.ExitLocated); } catch { }
                    }
                }
            }
            catch (Exception ex)
            {
                Logger.LogWarning("[MissionEvaluator] Extraction failed: " + ex.Message);
            }
        }

        #endregion

        #region Stuck Detection

        /// <summary>
        /// Overlay-only anti-stuck: triggers overlay fallback, never direct position set.
        /// Bulletproof, pooled. All logic must be externally BotBrain-ticked.
        /// </summary>
        public void UpdateStuckCheck(float time)
        {
            try
            {
                Vector3 current = EFTPlayerUtil.GetPosition(_bot);
                float moved = Vector3.Distance(current, _lastPos);

                if (moved > MinMoveThreshold)
                {
                    _lastPos = current;
                    _lastMoveTime = time;
                    _fallbackAttempts = 0;
                    return;
                }

                bool cooldownPassed = time - _stuckSince > StuckCooldown;
                bool stuckLongEnough = time - _lastMoveTime > StuckDuration;
                bool notRecentlyFellback = time - _lastStuckFallbackTime > StuckCooldown;

                if (stuckLongEnough && cooldownPassed && notRecentlyFellback && _fallbackAttempts < 2)
                {
                    _stuckSince = time;
                    _lastStuckFallbackTime = time;
                    _fallbackAttempts++;

                    if (BotNavHelper.TryGetSafeTarget(_bot, out Vector3 candidate) &&
                        NavMesh.SamplePosition(candidate, out var hit, NavSampleRadius, AIRefactoredLayerMasks.NavMeshAll))
                    {
                        Vector3 drifted = BotMovementHelper.ApplyMicroDrift(hit.position, _bot.ProfileId, Time.frameCount, _profile);
                        // Overlay/event-driven intent ONLY (never tick-move)
                        BotMovementHelper.SmoothMoveToSafe(_bot, drifted, slow: true, cohesion: 1f);
                    }
                }
            }
            catch (Exception ex)
            {
                Logger.LogError($"[MissionEvaluator] UpdateStuckCheck failed: {ex}");
            }
        }

        #endregion
    }
