        private Vector3 GetObjectiveTarget(MissionType type)
        {
            switch (type)
            {
                case MissionType.Quest:
                    return GetNextQuestObjective();
                case MissionType.Fight:
                    return GetFightZone();
                case MissionType.Loot:
                    return GetLootObjective();
                default:
