namespace AIRefactored.AI.Movement
{
    using System;
    using AIRefactored.AI.Core;
    using AIRefactored.Core;
    using AIRefactored.Pools;
    using EFT;
    using UnityEngine;

    /// <summary>
    /// Handles bot jumping, vaulting, and micro-failure on obstacles.
    /// Fully null-safe, pooled, and strictly BotBrain-tick-driven.
    /// All jumps use anticipation delays, random micro-mistakes, and error isolation.
    /// </summary>
    public sealed class BotJumpController
    {
        #region Constants

        private const float JumpCheckDistance = 1.08f;
        private const float JumpCooldown = 1.23f;
        private const float MaxJumpHeight = 1.19f;
        private const float MinJumpHeight = 0.32f;
        private const float ObstacleCheckRadius = 0.41f;
        private const float SafeFallHeight = 2.18f;
        private const float VaultForwardOffset = 0.73f;
        private const float JumpVelocityMultiplier = 1.46f;
        private const float HumanJumpDelayMin = 0.10f;
        private const float HumanJumpDelayMax = 0.23f;
        private const float HumanMistakeChance = 0.085f;
        private const float PanicCautionCooldown = 3.0f;
        private const float SuppressedCooldown = 1.7f;

        #endregion

        #region Fields

        private readonly BotOwner _bot;
        private readonly BotComponentCache _cache;
        private readonly MovementContext _context;

        private float _lastJumpTime;
        private float _nextAllowedJumpTime;
        private float _nextSuppressedCooldown;
        private bool _hasRecentlyJumped;
        private bool _isSuppressed;

        #endregion

        #region Constructor

        public BotJumpController(BotOwner bot, BotComponentCache cache)
        {
            if (!EFTPlayerUtil.IsValidBotOwner(bot) || cache == null)
                throw new ArgumentException("[BotJumpController] Invalid bot or cache.");

            MovementContext context = bot.GetPlayer?.MovementContext;
            if (context == null)
                throw new InvalidOperationException("[BotJumpController] Missing MovementContext.");

            _bot = bot;
            _cache = cache;
            _context = context;
        }

        #endregion

        #region Public Methods

        /// <summary>
        /// Main tick, called only by BotBrain.
        /// Handles anticipation, suppression, error-prone micro-delay, and performs jump or vault if valid.
        /// </summary>
        public void Tick(float deltaTime)
        {
            try
            {
                if (!IsJumpAllowed())
                    return;

                float now = Time.time;
                if (now < _nextAllowedJumpTime)
                    return;

                // Vision/suppression block: bots rarely jump when blinded or suppressed
                if (_cache != null && _cache.IsBlinded && now < _cache.BlindUntilTime)
                    return;
                if (_isSuppressed && now < _nextSuppressedCooldown)
                    return;

                Vector3[] temp = TempVector3Pool.Rent(1);
                try
                {
                    if (TryFindJumpTarget(out temp[0]))
                    {
                        // Simulate human error (occasional hesitation/abort)
                        if (UnityEngine.Random.value < HumanMistakeChance)
                        {
                            _nextAllowedJumpTime = now + UnityEngine.Random.Range(0.18f, 0.37f);
                            return;
                        }

                        float anticipation = UnityEngine.Random.Range(HumanJumpDelayMin, HumanJumpDelayMax);
                        _nextAllowedJumpTime = now + JumpCooldown + anticipation;
                        ExecuteJump(temp[0], deltaTime);
                    }
                }
                finally
                {
                    TempVector3Pool.Return(temp);
                }
            }
            catch
            {
                // All failures locally isolated
            }
        }

        #endregion

        #region Internal Logic

        /// <summary>
        /// Determines if a jump is currently permitted, based on pose, panic, group, suppression, cooldowns, etc.
        /// </summary>
        private bool IsJumpAllowed()
        {
            float now = Time.time;
            if (_hasRecentlyJumped && now - _lastJumpTime < JumpCooldown)
                return false;

            if (_context == null || !_context.IsGrounded || _context.IsInPronePose)
                return false;

            if (_bot.IsDead || _bot.Memory?.GoalEnemy != null)
                return false;

            // Panic/suppression: bots cannot jump while panicking, or for a cooldown after
            if (_cache != null)
            {
                if (_cache.PanicHandler != null)
                {
                    if (_cache.PanicHandler.IsPanicking)
                    {
                        _isSuppressed = true;
                        _nextSuppressedCooldown = now + PanicCautionCooldown;
                        _nextAllowedJumpTime = now + PanicCautionCooldown;
                        return false;
                    }
                }

                // Optionally, clear suppressed state if cooldown passed.
                if (_isSuppressed && now > _nextSuppressedCooldown)
                    _isSuppressed = false;
            }

            // Never jump if blocked by squadmate directly ahead (human-like queueing)
            if (_bot.BotsGroup != null && _bot.BotsGroup.MembersCount > 1)
            {
                Vector3 myPos = _bot.Position;
                Vector3 forward = _context.TransformForwardVector;
                float detectionRadius = 0.85f;
                for (int i = 0; i < _bot.BotsGroup.MembersCount; i++)
                {
                    BotOwner mate = _bot.BotsGroup.Member(i);
                    if (mate != null && mate != _bot && !mate.IsDead)
                    {
                        Vector3 toMate = mate.Position - myPos;
                        if (Vector3.Dot(forward, toMate.normalized) > 0.7f && toMate.magnitude < detectionRadius)
                            return false;
                    }
                }
            }

            _hasRecentlyJumped = false;
            return true;
        }

        /// <summary>
        /// Attempts to execute a human-like jump or vault towards the calculated target.
        /// </summary>
        private void ExecuteJump(Vector3 target, float deltaTime)
        {
            try
            {
                if (_context == null)
                    return;

                _context.OnJump();
                _lastJumpTime = Time.time;
                _hasRecentlyJumped = true;

                Vector3 direction = (target - _context.TransformPosition).normalized;
                if (direction.sqrMagnitude < 0.01f)
                    return;

                Vector3 velocity = direction * JumpVelocityMultiplier;
                _context.ApplyMotion(velocity, deltaTime);
            }
            catch
            {
                // Locally isolated
            }
        }

        /// <summary>
        /// Finds a valid jump/vault target ahead, using spherecast for obstacle and raycast for landing.
        /// </summary>
        private bool TryFindJumpTarget(out Vector3 target)
        {
            target = Vector3.zero;
            if (_context == null)
                return false;

            try
            {
                Vector3 origin = _context.PlayerColliderCenter + Vector3.up * 0.24f;
                Vector3 forward = _context.TransformForwardVector;

                RaycastHit[] hits = TempRaycastHitPool.Rent(1);
                try
                {
                    if (!Physics.SphereCast(origin, ObstacleCheckRadius, forward, out hits[0], JumpCheckDistance, AIRefactoredLayerMasks.ObstacleRayMask))
                        return false;

                    Bounds[] boundsArray = TempBoundsPool.Rent(1);
                    try
                    {
                        boundsArray[0] = hits[0].collider.bounds;
                        float height = boundsArray[0].max.y - _context.TransformPosition.y;

                        if (height < MinJumpHeight || height > MaxJumpHeight)
                            return false;

                        Vector3 vaultPoint = boundsArray[0].max + (forward * VaultForwardOffset);

                        RaycastHit[] landHits = TempRaycastHitPool.Rent(1);
                        try
                        {
                            if (!Physics.Raycast(vaultPoint, Vector3.down, out landHits[0], 2.55f, AIRefactoredLayerMasks.JumpRayMask))
                                return false;

                            float fallHeight = Mathf.Abs(_context.TransformPosition.y - landHits[0].point.y);
                            if (fallHeight > SafeFallHeight)
                                return false;

                            target = landHits[0].point;
                            return true;
                        }
                        finally
                        {
                            TempRaycastHitPool.Return(landHits);
                        }
                    }
                    finally
                    {
                        TempBoundsPool.Return(boundsArray);
                    }
                }
                finally
                {
                    TempRaycastHitPool.Return(hits);
                }
            }
            catch
            {
                // Failure always locally isolated
            }

            return false;
        }

        #endregion
    }
